mapper.insert:

insert into rboard (no, user_no, title, content, reg_date, group_no)
values(seq_rboard_no.nextval 
		,#{userNo} 
		,#{title} 
		,#{content} 
		,sysdate 
		,seq_rboard_groupNo.nextval)

mapper.update:

update rboard 
set title = #{title} 
	,content = #{content} 
	,reg_date = sysdate 
where no = #{no}


update rboard 
set order_no = order_no + 1 
where group_no = #{groupNo} 
and order_no > #{orderNo}


update rboard 
set hit = hit+1 
where no = #{rNo}
